test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source test
{
	type = mysql

	sql_query = SELECT 1, 'text';
}

index test
{
	source = test
	path = /test
	charset_type = utf-8
}

test.custom_test:
$opts = array
(
	'before_match' => '[B]',
	'after_match' => '[A]',
	'chunk_separator' => ' ... ',
	'limit' => 255,
	'around' => 2,
	'query_mode' => 1
);

$text = 'Sphinx clusters scale to billions of documents, terabytes of data, and billions of queries per month.';

$queries = array
(
	'^sphinx month$',
	'^sphinx queries$',
	'^clusters month$',
	'^*inx *bytes',
	'*i*',
	'*on*',
	'*s',
	'"clusters scale"',
	'"clusters do not scale"', // false claims don't get highlighted
	'"of d*"',
	'terabyte* << quer*',
	'data << terabyte*',
	'"sphinx scale"~3',
	'"sphinx billions"~3',
	'"silly documents"/1',
	'"clusters scale to billions"'
);

$results = array();
foreach ( $queries as $query )
{
	$reply = $client->BuildExcerpts ( array($text), 'test', $query, $opts );
	$results [] = $query;
	$results [] = $reply;
}
