test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
}

source srctest
{
	type			= mysql
	
	sql_query		= SELECT * FROM test_table
	sql_attr_uint	= gid
	sql_attr_float	= price
	sql_attr_uint	= views
}

source srctest2 : srctest
{
	sql_query		= SELECT document_id+10, body, gid, price, views FROM test_table
}


index test
{
	source			= srctest
	path			= /test
	docinfo			= extern
}

index dist
{
	type			= distributed
	local			= test
	agent			= :test2
}


index test2
{
	source			= srctest
	path			= /test2
	docinfo			= extern
}
